import React, { useCallback, useEffect, useState } from "react";
import Webcam from "react-webcam";   //npm install react-webcam
import "../styles/main.css";
import sound from "../assets/sound.png";
import send from "../assets/send.png";
import { useAuth0 } from "@auth0/auth0-react";
import { useSocket } from "../context/SocketProvider";
import ReactPlayer from 'react-player';
const CurrentChat=()=>{
  return (
    <div className="curr_chat">
      <input placeholder="type a message" type="text" />
      <button className="send">
      <img src={send} alt="404 error" />
      </button>
    </div>
  );
}
const Chathistory=()=>{
  return (
    <div className="oldchat">
    </div>
  );
}
const Chats=()=>{
  return(
    <div className="right">
      <h1 className="head">Chats</h1>
      <Chathistory/>
      <CurrentChat/>
    </div>

  );
}

const Main = ({userName}) => {
  // const [projectName,setProjectName]=useState('myprojectName');
  const webCamRef=React.useRef(null);
  const socket=useSocket();
  const [remoteSocketId,setRemoteSocketId]=useState(null);
  const handleUserJoined=useCallback(({email,id})=>{
    console.log(`Email ${email} joiined`);
    setRemoteSocketId(id);
  });
  const handleCallUser = useCallback(async () => {
    const stream = await navigator.mediaDevices.getUserMedia({
      audio: true,
      video: true,
    });
    // const offer = await peer.getOffer();
    // socket.emit("user:call", { to: remoteSocketId, offer });
    // setMyStream(stream);
  }, [remoteSocketId, socket]);
  useEffect(()=>{
    socket.on('user:joined',handleUserJoined);
    return ()=>{
      socket.off('user:joined',handleUserJoined);
    }
  },[socket,handleUserJoined]);
  const [duration,setDuration]=useState(0);
  return (
    <div id="bg">
      <div className="heading">
        <div className="title">
          <span>Welcome {userName}</span>
        </div>
        <div className="time">
          <div className="circle"></div>
          <span>5.23</span>
        </div>
        <div className="leave">
        {remoteSocketId && <button onClick={handleCallUser}>CALL</button>}
        </div>
      </div>
      <div className="body">
          <div className="left">
            <div className="videobig">
              <Webcam  className="userCam" audio={true} ref={webCamRef} mirrored={true}/>
              <div className="videosmall"></div>
            </div>
            <div className="subtitle">
              <div className="sound"><img src={sound} alt="notfound" /></div><span className="text">Subtitles..</span>
            </div>
          </div>
        <Chats/>
      </div>
    </div>
  );
};

export default Main;
